@Repository
@Transactional
public class TenantBasicDao
{
	@PersistenceContext
	protected EntityManager entityManager;

	public List<Tenant> findAllTenants() {
		CriteriaQuery<Tenant> criteria = entityManager.getCriteriaBuilder().createQuery(Tenant.class);
		criteria.select(criteria.from(Tenant.class));
		return entityManager.createQuery(criteria).getResultList();
	}

	public Tenant findById(Integer id) {
		return entityManager.find(Tenant.class, id);
	}

	public boolean update(Tenant entity) {
		try {
			entityManager.merge(entity);
			return true;
		} catch (Exception e) {
			return false;
		}
	}

	public Tenant findByName(String name)
	{
		CriteriaBuilder criteriaBuilder = entityManager.getCriteriaBuilder();
		CriteriaQuery<Tenant> criteria = criteriaBuilder.createQuery(Tenant.class);
		Root<Tenant> root = criteria.from(Tenant.class);
		criteria.select(root).where(criteriaBuilder.equal(root.get("name"), name));
		return entityManager.createQuery(criteria).getSingleResult();
	}


	public Tenant create(Tenant entity) {
		entityManager.persist(entity);
		return entity;
	}

	public void delete(Tenant entity) {
		entityManager.remove(entity);
	}
}